import React, { useState } from "react";
import { useQuery } from "react-query";
import Navbar from "../../Components/Navbar";
import CreateTab from "./CreateTab";
import { Input, Grid, Box, Icon, Skeleton, Spinner } from "@chakra-ui/core";

/**Fetch and return the data from https://intern-pokedex.myriadapps.com/api/v1/pokemon/?page=${page}&name=${queryString}*/
const fetchData = async (key) => {
  const response = await fetch(`http://tabsystem.test/api/tab`, {
    method: "Get",
    mode: "cors", // no-cors, *cors, same-origin
    cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
    credentials: "omit", // include, *same-origin, omit
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
      "content-type": "application/json",
    },
    redirect: "follow", // manual, *follow, error
    referrerPolicy: "no-referrer", // no-referrer, *client
  });

  const data = await response.json();
  console.log(data);
  return data;
};

export default function Tabs() {
  //Use the useQuery supported by react-query to fetch and cache the data.
  const { status, data, error } = useQuery("key", fetchData, {});

  //if loading return Loading with a spinner and skeleton
  if (status === "loading") {
    return (
      <div className="">
        <div>
          <Box textAlign="center" fontSize="50px" mt="60px">
            Loading... <Spinner />
          </Box>
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
        </div>
      </div>
    );
  }
  //if error return the error
  if (status === "error") return <div className="">error</div>;

  return (
    <div>
      <Navbar />
      <CreateTab users={data.users} />
      {data.tabs.map((tab) => (
        <div key={tab.id}>
          <TabCard
            id={tab.id}
            name={tab.name}
            user_name={tab.users.name}
            balance={tab.balance}
          />
        </div>
      ))}
    </div>
  );
}

function TabCard({ id, name, balance, user_name }) {
  return (
    <Box>
      <p>Name: {name}</p>
      <p>Name: {user_name}</p>
      <hr />
    </Box>
  );
}
