import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useQuery } from "react-query";

//styled component imports
import { Box, Icon, Grid, Skeleton, Spinner } from "@chakra-ui/core";
import { MyButton } from "../../GlobalStyles/Styles";

/** Returns the data from the api https://intern-pokedex.myriadapps.com/api/v1/pokemon/${id}*/
const fetchCommonItems = async (key) => {
  const response = await fetch(`http://tabsystem.test/api/commonitem`, {
    method: "Get",
    mode: "cors", // no-cors, *cors, same-origin
    cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
    credentials: "omit", // include, *same-origin, omit
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
      "content-type": "application/json",
    },
    redirect: "follow", // manual, *follow, error
    referrerPolicy: "no-referrer", // no-referrer, *client
  });
  const data = await response.json();
  console.log(data);
  return data;
};

export default function CreateCommonItem() {
  const [name, setName] = useState([]);
  const [category, setCategory] = useState([]);
  const [price, setPrice] = useState([]);
  const [message, setMessage] = useState("");
  const { status, data = [], error, refetch } = useQuery(
    "fetchCommonItems",
    fetchCommonItems,
    {}
  );

  const handleChange = (event) => {
    if ([event.target.name] == "name") {
      setName(event.target.value);
    } else if ([event.target.name] == "price") {
      setPrice(event.target.value);
    } else if ([event.target.name] == "category") {
      console.log(event.target.value);
      setCategory(event.target.value);
    }
  };
  const newCommonItem = async (e) => {
    e.preventDefault();
    const commonitem = {
      name: name,
      category_id: category,
      price: price,
    };
    console.log(commonitem);
    const response = await fetch(`http://tabsystem.test/api/commonitem`, {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "content-type": "application/json",
      },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
      body: JSON.stringify(commonitem),
    });
    const data = await response.json();
    console.log(data);
    if (data.success) {
      setMessage("Successfully created item");
    } else {
      setMessage("Something went wrong");
    }
  };
  //return this when loading
  if (status === "loading") {
    return (
      <div className="">
        <div>
          <Box textAlign="center" fontSize="50px" mt="60px">
            Loading... <Spinner />
          </Box>
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
        </div>
      </div>
    );
  }
  //return when error
  if (status === "error") return <div className="">error {error}</div>;

  return (
    <div>
      <Link to={`/tabs`}>Back</Link>
      {data.commonitems.map((commonitem) => (
        <div key={commonitem.id}>{commonitem.name}</div>
      ))}
      <div>
        <form onSubmit={newCommonItem}>
          <label>Name</label>
          <input type="text" onChange={handleChange} value={name} name="name" />
          <label>Price</label>
          <input
            type="text"
            onChange={handleChange}
            value={price}
            name="price"
          />
          <label htmlFor="category">Category</label>
          {/* Still have to hook up the value of the category with what is passed into the POST method */}
          <select name="category" id="category" onChange={handleChange}>
            {data.categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
          <MyButton
            bg="White"
            bgh="second100"
            bcf="fifth200"
            color="first200"
            type="submit"
          >
            Submit
          </MyButton>
        </form>
        {message}
      </div>
    </div>
  );
}
