import React, { useState } from "react";
import { Box } from "@chakra-ui/core";

export default function CreateItem({ tab_id, categories }) {
  const [name, setName] = useState("");
  const [categoryid, setCategoryid] = useState("");
  const [price, setPrice] = useState([]);
  const [message, setMessage] = useState([]);

  const handleChange = (event) => {
    if ([event.target.name] == "name") {
      setName(event.target.value);
    } else if ([event.target.name] == "price") {
      setPrice(event.target.value);
    }
  };

  const newItem = async (e) => {
    e.preventDefault();
    const item = {
      tab_id: tab_id,
      category_id: 1,
      name: name,
      price: parseInt(price),
    };
    console.log(item);
    const response = await fetch(`http://tabsystem.test/api/item`, {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "content-type": "application/json",
      },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
      body: JSON.stringify(item),
    });
    const data = await response.json();
    console.log(data);
    if (data.success) {
      setMessage("Successfully created item");
    } else {
      setMessage("Something went wrong");
    }
  };
  return (
    <div>
      <h3>Put a new item</h3>
      <div>
        <form onSubmit={newItem}>
          <label>Item Name</label>
          <input type="text" onChange={handleChange} value={name} name="name" />
          <label>Price</label>
          <input
            type="number"
            onChange={handleChange}
            value={price}
            name="price"
          />
          <label for="category">Category</label>
          <select name="category" id="category">
            {categories.map((category) => (
              <option value={category.id}>{category.name}</option>
            ))}
            <option value="volvo">Volvo</option>
            <option value="saab">Saab</option>
            <option value="opel">Opel</option>
            <option value="audi">Audi</option>
          </select>
          <button type="submit">Submit</button>
        </form>
      </div>
      {console.log(categories)}
      {message}
    </div>
  );
}
