class Auth {
  constructor() {
    this.authenticated = false;
    this.admin = false;
    this.message = "";
    this.registerMessage = "";
    if (localStorage.getItem("token")) {
      this.authenticated = true;
    }
    if (localStorage.getItem("token") && localStorage.getItem("admin")) {
      this.authenticated = true;
      this.admin = true;
    }
  }

  async register(cb, newUser) {
    const response = await fetch("http://tabsystem.test/api/register", {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: { "content-type": "application/json" },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
      body: JSON.stringify(newUser),
    });
    const data = await response.json();
    if (data.success) {
      this.registerMessage = "Success fully registered user.";
    } else {
      this.registerMessage = "Something went wrong";
    }
    cb();
  }

  async login(cb, user) {
    const response = await fetch("http://tabsystem.test/api/login", {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: { "content-type": "application/json" },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
      body: JSON.stringify(user),
    });
    const data = await response.json();
    if (data.success) {
      localStorage.setItem("token", data.data.api_token);
      this.authenticated = true;
      if (data.data.user.admin) {
        localStorage.setItem("admin", true);
        this.admin = true;
      }
    } else {
      this.message = "Username or password incorrect.";
      localStorage.removeItem("token");
      this.authenticated = false;
    }

    await cb();
  }

  async logout(cb) {
    const response = await fetch(`http://tabsystem.test/api/logout`, {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "content-type": "application/json",
      },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
    });
    this.authenticated = false;
    cb();
  }
  isAuthenticated() {
    return this.authenticated;
  }
  isAdmin() {
    return this.admin;
  }
  getMessage() {
    return this.message;
  }
  getRegisterMessage() {
    return this.registerMessage;
  }
}

export default new Auth();
