class Auth {
  constructor() {
    this.authenticated = false;
    this.message = "";

  }

  async register(cb, newUser) {
    this.authenticated = true;
    cb();
  }

  async login(cb, user) {
      const response = await fetch("http://tabsystem.test/api/login", {
          method: 'POST',
          body: JSON.stringify(user)
      })
    this.authenticated = true;
    await cb();
  }

  async logout(cb) {
    this.authenticated = false;
    cb();
  }
  isAuthenticated() {
    return this.authenticated;
  }
  getMessage(){
    return this.message;
  }
}

export default new Auth();
