import React, { useState } from "react";
import { PostItem } from "../../Api/Items";
import { Link } from "react-router-dom";
import { Box } from "@chakra-ui/core";

export default function CreateItem({ tab_id, categories, common_items }) {
  const [name, setName] = useState("");
  const [categoryid, setCategoryid] = useState(null);
  const [price, setPrice] = useState([]);
  const [message, setMessage] = useState([]);

  const handleChange = (event) => {
    if ([event.target.name] == "name") {
      setName(event.target.value);
    } else if ([event.target.name] == "price") {
      setPrice(event.target.value);
    } else if ([event.target.name] == "category") {
      console.log(event.target.value);
      if (event.target.value == 0) {
        setCategoryid(null);
      } else {
        setCategoryid(event.target.value);
      }
    } else if ([event.target.name] == "commonitem") {
      console.log(event.target.value);
      let commonitem = common_items.filter(
        (myitem) => myitem.id == event.target.value
      );
      if (commonitem.length != 1) {
        commonitem = [{ name: "", price: 0, category_id: null }];
      }
      console.log(commonitem[0]);
      setName(commonitem[0].name);
      setPrice(commonitem[0].price);
      setCategoryid(commonitem[0].category_id);
    }
  };

  const newItem = async (e) => {
    e.preventDefault();
    const item = {
      tab_id: tab_id,
      category_id: categoryid,
      name: name,
      price: parseInt(price),
    };

    const data = await PostItem(item);
    console.log(data);
    if (data.success) {
      setMessage("Successfully created item");
    } else {
      setMessage("Something went wrong");
    }
  };
  return (
    <div>
      <h3>Put a new item</h3>
      <div>
        <form onSubmit={newItem}>
          <label htmlFor="commonitem">Presets</label>
          <select name="commonitem" id="commonitem" onChange={handleChange}>
            <option value={null}>none</option>
            {common_items.map((commonitem) => (
              <option key={commonitem.id} value={commonitem.id}>
                {commonitem.name}
              </option>
            ))}
          </select>
          <label>Item Name</label>
          <input type="text" onChange={handleChange} value={name} name="name" />
          <label>Price</label>
          <input
            type="number"
            onChange={handleChange}
            value={price}
            name="price"
          />
          <label htmlFor="category">Category</label>
          <select name="category" id="category" onChange={handleChange}>
            <option value={0}>none</option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
          <button type="submit">Submit</button>
        </form>
      </div>
      {message}
    </div>
  );
}
