import Navbar from "../../Components/Navbar";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useQuery, useMutation, queryCache } from "react-query";
import { GetTabs } from "../../Api/Tabs";
import { GetUsers } from "../../Api/Users";
import { GetItems } from "../../Api/Items";
import { GetCategories } from "../../Api/Categories";
import TabCard from "../TabDetail/TabCard";
import {
  Input,
  Grid,
  Box,
  Icon,
  Skeleton,
  Spinner,
  Select,
} from "@chakra-ui/core";

export default function Dashboard() {
  const [category, setCategory] = useState("");
  //Use the useQuery supported by react-query to fetch and cache the data.
  const { status: status1, data: tabsData, refetch: refetchTabs } = useQuery(
    "GetTabs",
    GetTabs,
    {}
  );
  const { status: status2, data: usersData } = useQuery(
    "GetUsers",
    GetUsers,
    {}
  );
  const { status: status3, data: itemsData } = useQuery(
    ["GetItems", category],
    GetItems,
    {}
  );
  const { status: status4, data: categoryData } = useQuery(
    "GetCategories",
    GetCategories,
    {}
  );

  //if loading return Loading with a spinner and skeleton
  if (
    status1 === "loading" ||
    status2 === "loading" ||
    status3 === "loading" ||
    status4 === "loading"
  ) {
    return (
      <Box minH="100vh" bg="first300">
        <Box textAlign="center" fontSize="50px" mt="60px">
          Loading... <Spinner />
        </Box>
        <Skeleton height="50px" my="10px" mx="10%" />
        <Skeleton height="50px" my="10px" mx="10%" />
        <Skeleton height="50px" my="10px" mx="10%" />
      </Box>
    );
  }
  //if error return the error
  if (
    status1 === "error" ||
    status2 === "error" ||
    status3 === "error" ||
    status4 === "error"
  ) {
    return <div className="">error</div>;
  }
  return (
    <div>
      <Navbar />
      <h1>Amount of tabs = {tabsData.length}</h1>
      <h1>Amount of users = {usersData.length}</h1>
      <h1>Amount of items on tabs= {itemsData.length}</h1>
      <h1>Amount of money on all tabs= </h1>

      <h1>Total price of all items= </h1>
      <h1>Amount of items per category= </h1>
      <Box d="grid" gridTemplateColumns="1fr 2fr">
        <Box htmlFor="category" color="White" fontSize="25px">
          Category
        </Box>
        <Select
          name="category"
          id="category"
          onChange={({ target }) => setCategory(target.value)}
          width="200px"
          height="30px"
          color="black"
          value={category}
        >
          <option value={""}>none</option>
          {categoryData.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </Select>
      </Box>
    </div>
  );
}
