import React, { useState } from "react";
import { Box } from "@chakra-ui/core";
import { DeleteButton } from "../../GlobalStyles/Styles";
import { MyTd, MyTable, MyTh, MyTr } from "./Styles";
import { DeleteItemWithRefund, DeleteItemWithoutRefund } from "../../Api/Items";
import moment from "moment";

export default function Item({
  id,
  name,
  price,
  date,
  category,
  queryCache,
  handleClick,
}) {
  const [selected, setSelected] = useState(false);

  const select = () => {
    handleClick(id, selected);
    selected ? setSelected(false) : setSelected(true);
  };
  const removeItem = async () => {
    const data = await DeleteItemWithoutRefund(id);
    queryCache.refetchQueries("GetTabById");
    queryCache.refetchQueries("GetUserOfTab");
    console.log(data);
  };
  const removeItemWithRefund = async () => {
    const data = await DeleteItemWithRefund(id);
    queryCache.refetchQueries("GetTabById");
    queryCache.refetchQueries("GetUserOfTab");
    console.log(data);
  };
  const formatedDate = () => {
    return moment(date).format("MM/DD");
  };
  return (
    <MyTr selected={selected} onClick={select}>
      <MyTd>{formatedDate()}</MyTd>
      <MyTd>{name}</MyTd>
      <MyTd>{category ? category.name : "none"}</MyTd>
      <MyTd>{price}</MyTd>
    </MyTr>
  );
}
