import styled from "@emotion/styled";
import tabtheme from "./Theme";

export const MyButton = styled.button`
  background-color: ${(props) => tabtheme.colors[props.bg]};
  color: ${(props) => tabtheme.colors[props.color]};
  border: none;
  padding: 10px;
  margin: 5px;
  border-radius: 10px;
  outline: none;
  &:hover {
    background-color: ${(props) => tabtheme.colors[props.bgh]};
    transition: background-color 0.5s ease;
  }
  &:focus {
    border: 2px solid lightcoral;
    transition: border 0.1s ease;
  }
`;
