import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useQuery, queryCache, useMutation } from "react-query";
import {
  GetCategories,
  PostCategory,
  DeleteCategory,
} from "../../Api/Categories";
//styled component imports
import { Box, Icon, Grid, Skeleton, Spinner, Input } from "@chakra-ui/core";
import { MyButton } from "../../GlobalStyles/Styles";
import { DeleteButton, CategoryCard } from "./Styles";

import Navbar from "../../Components/Navbar";

export default function CreateCategory() {
  const [name, setName] = useState([]);
  const [message, setMessage] = useState("");
  const { status, data, error, refetch } = useQuery(
    "GetCategories",
    GetCategories,
    {}
  );
  const [mutatePostCategory] = useMutation(PostCategory, {
    onMutate: (newCategory) => {
      // Cancel any outgoing refetches (so they don't overwrite our optimistic update)
      queryCache.cancelQueries("GetCategories");

      // Snapshot the previous value
      const previousCateogories = queryCache.getQueryData("GetCategories");

      // Optimistically update to the new value
      queryCache.setQueryData("GetCategories", (old) => [...old, newCategory]);

      // Return the snapshotted value
      return () =>
        queryCache.setQueryData("GetCategories", previousCateogories);
    },
    // If the mutation fails, use the value returned from onMutate to roll back
    onError: (err, newCategory, rollback) => rollback(),
    // Always refetch after error or success:
    onSettled: () => {
      queryCache.refetchQueries("GetCategories");
    },
  });
  const [mutateDeleteCategory] = useMutation(DeleteCategory, {
    // When mutate is called:
    onMutate: (categoryIdPassed) => {
      // Cancel any outgoing refetches (so they don't overwrite our optimistic update)
      queryCache.cancelQueries("GetCategories");
      // Snapshot the previous value
      const previousCateogories = queryCache.getQueryData("GetCategories");
      // Return the snapshotted value
      return () =>
        queryCache.setQueryData("GetCategories", previousCateogories);
    },
    // If the mutation fails, use the value returned from onMutate to roll back
    onError: (err, categoryIdPassed, rollback) => rollback(),
    // Always refetch after error or success:
    onSettled: () => {
      queryCache.refetchQueries("GetCategories");
    },
  });

  const handleChange = (event) => {
    setName(event.target.value);
  };
  const newCategory = async (e) => {
    e.preventDefault();
    const category = {
      name: name,
    };
    const data = await mutatePostCategory(category);
    console.log(data);
    setName("");
    if (data.success) {
      setMessage("Successfully created item");
    } else {
      setMessage("Something went wrong");
    }
  };

  const deleteCategory = async (id) => {
    const data = await mutateDeleteCategory(id);
    console.log(data);
    if (data.success) {
      setMessage("Successfully deleted item");
    } else {
      setMessage("Something went wrong");
    }
  };
  //return this when loading
  if (status === "loading") {
    return (
      <div className="">
        <div>
          <Box textAlign="center" fontSize="50px" mt="60px">
            Loading... <Spinner />
          </Box>
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
        </div>
      </div>
    );
  }
  //return when error
  if (status === "error") return <div className="">error {error}</div>;

  return (
    <Box bg="fourth100" minH="100vh">
      <Navbar />
      <Box mt="40px">
        <form onSubmit={newCategory} autoComplete="off">
          <Box d="flex" justifyContent="space-around" alignItems="space-around">
            <Box color="White" fontSize="30px">
              category name
            </Box>
            <Input
              width="200px"
              height="30px"
              type="text"
              onChange={handleChange}
              value={name}
              name="name"
            />

            <MyButton
              bg="White"
              bgh="second100"
              bcf="fifth200"
              color="first200"
              type="submit"
            >
              Submit
            </MyButton>
          </Box>
        </form>
        {message}
      </Box>
      <Grid templateColumns="repeat(auto-fit, minmax(200px, 1fr))">
        {data.map((category, index) => (
          <CategoryCard key={index}>
            <Box bg="first300" p="1px 20px">
              {category.name}
            </Box>
            <DeleteButton onClick={() => deleteCategory(category.id)}>
              Delete
            </DeleteButton>
          </CategoryCard>
        ))}
      </Grid>
    </Box>
  );
}
