import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useQuery, useMutation, queryCache } from "react-query";
import { GetTabs, PostTab } from "../../Api/Tabs";
import { GetUsers } from "../../Api/Users";
import Navbar from "../../Components/Navbar";
import TabCard from "../TabDetail/TabCard";
import { Input, Grid, Box, Icon, Skeleton, Spinner } from "@chakra-ui/core";
import { MyButton } from "../../GlobalStyles/Styles";

export default function Tabs() {
  const [selected, setSelected] = useState(null);
  const [nameOnTab, setNameOnTab] = useState("");
  const [balance, setBalance] = useState([]);
  const [message, setMessage] = useState([]);
  //Use the useQuery supported by react-query to fetch and cache the data.
  const { status: status1, data: tabsData } = useQuery("GetTabs", GetTabs, {});
  const { status: status2, data: usersData } = useQuery(
    "GetUsers",
    GetUsers,
    {}
  );

  const [mutate] = useMutation(PostTab, {
    onMutate: (newTab) => {
      // Cancel any outgoing refetches (so they don't overwrite our optimistic update)
      queryCache.cancelQueries("GetTabs");

      // Snapshot the previous value
      const previousTabs = queryCache.getQueryData("GetTabs");

      // Optimistically update to the new value
      queryCache.setQueryData("GetTabs", (old) => [...old, newTab]);

      // Return the snapshotted value
      return () => queryCache.setQueryData("GetTabs", previousTabs);
    },
    // If the mutation fails, use the value returned from onMutate to roll back
    onError: (err, newTab, rollback) => rollback(),
    // Always refetch after error or success:
    onSettled: () => {
      queryCache.invalidateQueries("GetTabs");
    },
  });
  const select = (userid) => {
    setSelected(userid);
  };
  const handleChange = (event) => {
    if ([event.target.name] == "name") {
      setNameOnTab(event.target.value);
    } else if ([event.target.name] == "balance") {
      setBalance(event.target.value);
    }
  };
  const stopSelect = () => {
    setSelected(null);
  };
  const newTab = async (e) => {
    e.preventDefault();
    const tab = {
      user_id: selected,
      name: nameOnTab,
      balance: parseInt(balance),
    };

    const data = await mutate(tab);
    console.log(data);
    if (data.success) {
      setMessage("Successfully created tab");
    }
  };

  //if loading return Loading with a spinner and skeleton
  if (status1 === "loading" || status2 === "loading") {
    return (
      <div className="">
        <div>
          <Box textAlign="center" fontSize="50px" mt="60px">
            Loading... <Spinner />
          </Box>
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
        </div>
      </div>
    );
  }
  //if error return the error
  if (status1 === "error" || status2 === "error") {
    return <div className="">error</div>;
  }

  return (
    <div>
      <Navbar />
      <div>
        <h3>Create a new tab for someone</h3>
        <Box
          display="grid"
          gridTemplateColumns="repeat(auto-fit, minmax(300px, 1fr))"
        >
          {usersData.map((user) => {
            if (user.id === selected) {
              return (
                <Box
                  bg="first100"
                  color="White"
                  margin="20px"
                  padding="20px"
                  textAlign="center"
                  onClick={() => select(user.id)}
                  cursor="pointer"
                  key={user.id}
                >
                  <Box> Name: {user.name}</Box>
                  <Box> Site Number: {user.site_number}</Box>
                </Box>
              );
            }
            return (
              <Box
                bg="second100"
                color="White"
                margin="20px"
                padding="20px"
                textAlign="center"
                onClick={() => select(user.id)}
                cursor="pointer"
                key={user.id}
              >
                <Box> Name: {user.name}</Box>
                <Box> Site Number: {user.site_number}</Box>
              </Box>
            );
          })}
        </Box>

        {selected && (
          <div>
            <form onSubmit={newTab}>
              <label>Name of tab </label>
              <input
                type="text"
                onChange={handleChange}
                value={nameOnTab}
                name="name"
              />
              <label>Starting Balance</label>
              <input
                type="number"
                onChange={handleChange}
                value={balance}
                name="balance"
              />
              <MyButton type="submit">Submit</MyButton>
            </form>
            <MyButton onClick={stopSelect}>No Select</MyButton>
          </div>
        )}
        {message}
      </div>
      <Box
        display="grid"
        gridTemplateColumns="repeat(auto-fit, minmax(300px, 1fr))"
        mx="20px"
        py="20px"
      >
        {tabsData.tabs.map((tab) => (
          <Link
            key={tab.id}
            to={`/tabs/detail/${tab.id}`}
            style={{
              textDecoration: "none",
              color: "black",
              margin: "20px 0px",
            }}
          >
            <div>
              <p>Tab Name: {tab.name}</p>
              {/* <p>Site # {tab.users.site_number}</p>
              <p>User's Name: {tab.users.name}</p> */}
              <p>Balance: {tab.balance}</p>
            </div>
          </Link>
        ))}
      </Box>
    </div>
  );
}
