import React, { useState } from "react";
import Item from "./Item";
import CreateItem from "./CreateItem";
import { Box, Grid } from "@chakra-ui/core";
import { MyTd, MyTable, MyTh } from "./Styles";
import { DeleteButton } from "../../GlobalStyles/Styles";
import { DeleteItemWithRefund, DeleteItemWithoutRefund } from "../../Api/Items";

export default function TabCard({
  id,
  name,
  balance,
  user_name,
  items,
  site_number,
  categories,
  common_items,
  newItem,
  queryCache,
}) {
  const [allSelected, setAllSelected] = useState([]);

  const handleClick = (newId, selected) => {
    console.log(allSelected);
    console.log(selected);
    if (selected !== "true") {
      console.log("selected is true");
      const ids = allSelected.filter((id, index, self) => {
        return self.indexOf(id) === index;
      });
      setAllSelected(ids);
    } else {
      setAllSelected([...allSelected, newId]);
    }
    //setAllSelected([...allSelected, newId]);
  };
  const removeItem = async () => {
    for (let i = 0; i < allSelected.length; i++) {
      const data = await DeleteItemWithoutRefund(allSelected[i]);
      console.log(data);
    }
    //const data = await DeleteItemWithoutRefund(id);
    queryCache.refetchQueries("GetTabById");
    queryCache.refetchQueries("GetUserOfTab");
  };
  const removeItemWithRefund = async () => {
    for (let i = 0; i < allSelected.length; i++) {
      const data = await DeleteItemWithRefund(allSelected[i]);
      console.log(data);
    }
    //const data = await DeleteItemWithoutRefund(id);
    queryCache.refetchQueries("GetTabById");
    queryCache.refetchQueries("GetUserOfTab");
  };

  return (
    <Box textAlign="center" color="white">
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="space-around"
        minH="200px"
        bg="first100"
        border="none"
        rounded="10px"
        mx="5%"
        my="30px"
        fontSize="30px"
      >
        <p>Tab Name: {name}</p>
        <p>Site # {site_number}</p>
        <p>User's Name: {user_name}</p>
        <p>Balance: {balance}</p>
      </Box>
      <CreateItem
        tab_id={id}
        categories={categories}
        common_items={common_items}
        newItem={newItem}
      />

      <Box fontSize="30px" mb="30px">
        Tab Items:
      </Box>
      <DeleteButton onClick={removeItemWithRefund}>del w/refund</DeleteButton>
      <DeleteButton onClick={removeItem}>del</DeleteButton>
      <Box>
        <MyTable>
          <tr>
            <MyTh>Date</MyTh>
            <MyTh>Name</MyTh>
            <MyTh>Category</MyTh>
            <MyTh>Price</MyTh>
          </tr>

          {items.map((item, index) => (
            <Item
              key={index}
              id={item.id}
              name={item.name}
              price={item.price}
              date={item.created_at}
              category={item.category}
              queryCache={queryCache}
              handleClick={handleClick}
            />
          ))}
        </MyTable>
      </Box>
    </Box>
  );
}
