import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Box } from "@chakra-ui/core";

export default function CreateItem({ tab_id, categories, common_items }) {
  const [name, setName] = useState("");
  const [categoryid, setCategoryid] = useState(categories[0].id);
  const [price, setPrice] = useState([]);
  const [message, setMessage] = useState([]);

  const handleChange = (event) => {
    if ([event.target.name] == "name") {
      setName(event.target.value);
    } else if ([event.target.name] == "price") {
      setPrice(event.target.value);
    } else if ([event.target.name] == "category") {
      setCategoryid(event.target.value);
    } else if ([event.target.name] == "commonitem") {
      console.log(event.target.value);
      const commonitem = common_items.filter(
        (myitem) => myitem.id == event.target.value
      );
      console.log(commonitem);
      setName(commonitem[0].name);
      setPrice(commonitem[0].price);
      setCategoryid(commonitem[0].category_id);
    }
  };

  const newItem = async (e) => {
    e.preventDefault();
    const item = {
      tab_id: tab_id,
      category_id: categoryid,
      name: name,
      price: parseInt(price),
    };
    console.log(item);
    const response = await fetch(`http://tabsystem.test/api/item`, {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "content-type": "application/json",
      },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
      body: JSON.stringify(item),
    });
    const data = await response.json();
    console.log(data);
    if (data.success) {
      setMessage("Successfully created item");
      window.location.reload();
    } else {
      setMessage("Something went wrong");
    }
  };
  return (
    <div>
      <h3>Put a new item</h3>
      <div>
        <form onSubmit={newItem}>
          <label htmlFor="commonitem">Presets</label>
          <select name="commonitem" id="commonitem" onChange={handleChange}>
            {common_items.map((commonitem) => (
              <option key={commonitem.id} value={commonitem.id}>
                {commonitem.name}
              </option>
            ))}
          </select>
          <label>Item Name</label>
          <input type="text" onChange={handleChange} value={name} name="name" />
          <label>Price</label>
          <input
            type="number"
            onChange={handleChange}
            value={price}
            name="price"
          />
          <label htmlFor="category">Category</label>
          <select name="category" id="category" onChange={handleChange}>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
          <button type="submit">Submit</button>
        </form>
        <Link to={`/createcategory`}>create category</Link>
      </div>
      {message}
    </div>
  );
}
