import React, { useState } from "react";
import { Formik } from "formik";
import * as Yup from "yup";
import Navbar from "../Navbar";
import Auth from "../../Auth";

const validationScema = Yup.object().shape({
  email: Yup.string()
    .email("Must be a valid email")
    .required("Must enter an email"),
  password: Yup.string()
    .min(4, "All passwords are at least 4 characters long")
    .required("Must have a password"),
});

export default function Login(props) {
  const [message, setMessage] = useState(Auth.getMessage());

  return (
    <div>
      <Navbar />
      <div>
        <h1>Login to Pokedex</h1>
        <p>{message}</p>
        <Formik
          initialValues={{ email: "", password: "" }}
          validationSchema={validationScema}
          onSubmit={(values, { setSubmitting, resetForm }) => {
            setSubmitting(true);
            const user = {
              email: values.email,
              password: values.password,
            };

            Auth.login(() => {
              props.history.push("/dashboard");
            }, user);
            console.log(Auth.getMessage());
            setMessage(Auth.getMessage());
            resetForm();
            setSubmitting(false);
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
            isSubmitting,
          }) => (
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="text"
                  name="email"
                  id="email"
                  placeholder="example@gmail.com"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.email}
                />
                <Error touched={touched.email} message={errors.email} />
              </div>
              <div>
                <label htmlFor="password">Password</label>
                <input
                  className="form-control"
                  type="password"
                  name="password"
                  id="password"
                  placeholder="password"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.password}
                />
                <Error touched={touched.password} message={errors.password} />
              </div>
              <button
                className="btn btn-primary"
                type="submit"
                disabled={isSubmitting}
              >
                Login
              </button>
            </form>
          )}
        </Formik>
      </div>
    </div>
  );
}

function Error({ touched, message }) {
  if (!touched) {
    return <div style={{ color: "red" }}>&nbsp;</div>;
  }
  if (message) {
    return <div style={{ color: "red" }}>{message}</div>;
  }
  return <div style={{ color: "green" }}>all good</div>;
}
