import React, { useState } from "react";
import { Box } from "@chakra-ui/core";
import { MyButton } from "../../GlobalStyles/Styles";

export default function CreateTab({ users }) {
  const [selected, setSelected] = useState(null);
  const [nameOnTab, setNameOnTab] = useState("");
  const [balance, setBalance] = useState([]);
  const [message, setMessage] = useState([]);
  const select = (userid) => {
    setSelected(userid);
  };
  const handleChange = (event) => {
    if ([event.target.name] == "name") {
      setNameOnTab(event.target.value);
    } else if ([event.target.name] == "balance") {
      setBalance(event.target.value);
    }
  };
  const stopSelect = () => {
    setSelected(null);
  };

  const newTab = async (e) => {
    e.preventDefault();
    const tab = {
      user_id: selected,
      name: nameOnTab,
      balance: parseInt(balance),
    };
    console.log(tab);
    const response = await fetch(`http://tabsystem.test/api/tab`, {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "content-type": "application/json",
      },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
      body: JSON.stringify(tab),
    });
    const data = await response.json();
    console.log(data);
    if (data.success) {
      setMessage("Successfully created tab");
    }
  };
  return (
    <div>
      <h3>Create a new tab for someone</h3>
      <Box
        display="grid"
        gridTemplateColumns="repeat(auto-fit, minmax(300px, 1fr))"
      >
        {users.map((user) => {
          if (user.id === selected) {
            return (
              <Box
                bg="first100"
                color="White"
                margin="20px"
                padding="20px"
                textAlign="center"
                onClick={() => select(user.id)}
                cursor="pointer"
                key={user.id}
              >
                <Box> Name: {user.name}</Box>
                <Box> Site Number: {user.site_number}</Box>
              </Box>
            );
          }
          return (
            <Box
              bg="second100"
              color="White"
              margin="20px"
              padding="20px"
              textAlign="center"
              onClick={() => select(user.id)}
              cursor="pointer"
              key={user.id}
            >
              <Box> Name: {user.name}</Box>
              <Box> Site Number: {user.site_number}</Box>
            </Box>
          );
        })}
      </Box>

      {selected && (
        <div>
          <form onSubmit={newTab}>
            <label>Name of tab </label>
            <input
              type="text"
              onChange={handleChange}
              value={nameOnTab}
              name="name"
            />
            <label>Starting Balance</label>
            <input
              type="number"
              onChange={handleChange}
              value={balance}
              name="balance"
            />
            <MyButton type="submit">Submit</MyButton>
          </form>
          <MyButton onClick={stopSelect}>No Select</MyButton>
        </div>
      )}
      {message}
    </div>
  );
}
