import React from "react";
import Item from "./Item";
import CreateItem from "./CreateItem";
import { Box } from "@chakra-ui/core";

export default function TabCard({ id, name, balance, user_name, items }) {
  return (
    <Box border="1px solid black" textAlign="center">
      <p>Tab Name: {name}</p>
      <p>User's Name: {user_name}</p>
      <p>Balance: {balance}</p>
      <Box borderBottom="1px solid gray" mx="5%" />
      <p>Tab Items: </p>
      {items.map((item) => (
        <div>
          <Item
            id={item.id}
            name={item.name}
            price={item.price}
            date={item.created_at}
            category={item.category.name}
          />
        </div>
      ))}
      <Box borderBottom="1px solid gray" mx="5%" />
      <p>Add a new Item</p>
      <CreateItem tab_id={id} />
    </Box>
  );
}
