//state handler imports
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useQuery, useMutation, queryCache } from "react-query";
import { GetTabById } from "../../Api/Tabs";
import { GetItems, PostItem } from "../../Api/Items";
import { GetCategories } from "../../Api/Categories";
import { GetCommonItems } from "../../Api/CommonItems";
import { GetUserOfTab } from "../../Api/Users";

//styled component imports
import { Box, Icon, Grid, Skeleton, Spinner } from "@chakra-ui/core";

//component imports
import TabCard from "./TabCard";

export default function TabDetail({ history, match }) {
  //set my id state which is either 1 or the id specified in the url
  const [id, setId] = useState(
    !match.params.id ? 1 : parseInt(match.params.id)
  );

  //fetch the data using id as a paramerter to find what data to return.
  const { status: status1, data: tabData } = useQuery(
    ["GetTabById", id],
    GetTabById,
    {}
  );
  const { status: status2, data: itemsData } = useQuery(
    ["GetItems", id],
    GetItems,
    {}
  );
  const { status: status3, data: categoriesData } = useQuery(
    ["GetCategories", id],
    GetCategories,
    {}
  );
  const { status: status4, data: commonitemsData } = useQuery(
    ["GetCommonItems", id],
    GetCommonItems,
    {}
  );
  const { status: status5, data: UserData } = useQuery(
    ["GetUserOfTab", id],
    GetUserOfTab,
    {}
  );

  const [mutate] = useMutation(PostItem, {
    onMutate: (myNewItem) => {
      // Cancel any outgoing refetches (so they don't overwrite our optimistic update)
      queryCache.cancelQueries("GetItems");

      // Snapshot the previous value
      const previousItems = queryCache.getQueryData("GetItems");
      // Optimistically update to the new value
      queryCache.setQueryData("GetItems", (old) => [...old, myNewItem]);

      // Return the snapshotted value
      return () => queryCache.setQueryData("GetItems", previousItems);
    },
    // If the mutation fails, use the value returned from onMutate to roll back
    onError: (err, myNewItem, rollback) => rollback(),
    // Always refetch after error or success:
    onSettled: () => {
      queryCache.refetchQueries("GetItems");
      queryCache.refetchQueries("GetTabById");
    },
  });

  const newItem = async (item) => {
    const data = await mutate(item);
    // console.log(data);
    return data;
  };

  //return this when loading
  if (
    status1 === "loading" ||
    status2 === "loading" ||
    status3 === "loading" ||
    status4 === "loading" ||
    status5 === "loading"
  ) {
    return (
      <div className="">
        <div>
          <Box textAlign="center" fontSize="50px" mt="60px">
            Loading... <Spinner />
          </Box>
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
        </div>
      </div>
    );
  }
  //return when error
  if (
    status1 === "error" ||
    status2 === "error" ||
    status3 === "error" ||
    status4 === "error" ||
    status5 === "error"
  ) {
    return <div className="">error </div>;
  }

  return (
    <div>
      <Link to={`/tabs`}>Back</Link>
      <TabCard
        id={tabData.tab.id}
        name={tabData.tab.name}
        user_name={UserData.name}
        balance={tabData.tab.balance}
        site_number={UserData.site_number}
        items={itemsData}
        categories={categoriesData}
        common_items={commonitemsData}
        newItem={newItem}
      />
    </div>
  );
}
