import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useQuery, useMutation, queryCache } from "react-query";
import { GetTabs } from "../../Api/Tabs";
import { GetUsers } from "../../Api/Users";
import Navbar from "../../Components/Navbar";
import CreateTab from "./CreateTab";
import TabCard from "../TabDetail/TabCard";
import { Input, Grid, Box, Icon, Skeleton, Spinner } from "@chakra-ui/core";

export default function Tabs() {
  //Use the useQuery supported by react-query to fetch and cache the data.
  const { status: status1, data: tabsData } = useQuery("GetTabs", GetTabs, {});
  const { status: status2, data: usersData } = useQuery(
    "GetUsers",
    GetUsers,
    {}
  );

  const newTab = async (e) => {
    e.preventDefault();
    console.log(e);
    const tab = {
      // user_id: selected,
      // name: nameOnTab,
      // balance: parseInt(balance),
    };
    console.log(tab);
    const response = await fetch(`http://tabsystem.test/api/tab`, {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "content-type": "application/json",
      },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
      body: JSON.stringify(tab),
    });
    const data = await response.json();
    console.log(data);
    if (data.success) {
      //setMessage("Successfully created tab");
    }
  };

  //if loading return Loading with a spinner and skeleton
  if (status1 === "loading" || status2 === "loading") {
    return (
      <div className="">
        <div>
          <Box textAlign="center" fontSize="50px" mt="60px">
            Loading... <Spinner />
          </Box>
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
        </div>
      </div>
    );
  }
  //if error return the error
  if (status1 === "error" || status2 === "error") {
    return <div className="">error</div>;
  }

  return (
    <div>
      <Navbar />
      <CreateTab users={usersData} newTab={newTab} />
      <Box
        display="grid"
        gridTemplateColumns="repeat(auto-fit, minmax(300px, 1fr))"
        mx="20px"
        py="20px"
      >
        {tabsData.tabs.map((tab) => (
          <Link
            key={tab.id}
            to={`/tabs/detail/${tab.id}`}
            style={{
              textDecoration: "none",
              color: "black",
              margin: "20px 0px",
            }}
          >
            <div>
              <p>Tab Name: {tab.name}</p>
              {/* <p>Site # {tab.users.site_number}</p>
              <p>User's Name: {tab.users.name}</p> */}
              <p>Balance: {tab.balance}</p>
            </div>
          </Link>
        ))}
      </Box>
    </div>
  );
}
