import styled from "@emotion/styled";

export const NavbarStyle = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  padding: 15px;
  background-color: ${(props) => props.typeColor};
`;

export const NavbarLink = styled.a`
  text-decoration: none;
  color: white;
  margin: 0px 20px;
  &:hover{
      background-color: teal;
  }
`;
