import React, { useState } from "react";
import { useQuery } from "react-query";
import Navbar from "../../Components/Navbar";
import CreateTab from "./CreateTab";
import { Input, Grid, Box, Icon, Skeleton, Spinner } from "@chakra-ui/core";

/**Fetch and return the data from https://intern-pokedex.myriadapps.com/api/v1/pokemon/?page=${page}&name=${queryString}*/
const fetchData = async (key) => {
  const response = await fetch(`http://tabsystem.test/api/tab`, {
    method: "Get",
    mode: "cors", // no-cors, *cors, same-origin
    cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
    credentials: "omit", // include, *same-origin, omit
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
      "content-type": "application/json",
    },
    redirect: "follow", // manual, *follow, error
    referrerPolicy: "no-referrer", // no-referrer, *client
  });

  const data = await response.json();
  console.log(data);
  return data;
};

export default function Tabs() {
  //Use the useQuery supported by react-query to fetch and cache the data.
  const { status, data, error } = useQuery("key", fetchData, {});

  //if loading return Loading with a spinner and skeleton
  if (status === "loading") {
    return (
      <div className="">
        <div>
          <Box textAlign="center" fontSize="50px" mt="60px">
            Loading... <Spinner />
          </Box>
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
        </div>
      </div>
    );
  }
  //if error return the error
  if (status === "error") return <div className="">error</div>;

  return (
    <div>
      <Navbar />
      <CreateTab users={data.users} />
      <Box
        display="grid"
        gridTemplateColumns="repeat(auto-fit, minmax(300px, 1fr))"
        mx="20px"
        py="20px"
      >
        {data.tabs.map((tab) => (
          <div key={tab.id}>
            <TabCard
              id={tab.id}
              name={tab.name}
              user_name={tab.users.name}
              balance={tab.balance}
              items={tab.items}
            />
          </div>
        ))}
      </Box>
    </div>
  );
}

function TabCard({ id, name, balance, user_name, items }) {
  return (
    <Box border="1px solid black" textAlign="center">
      <p>Tab Name: {name}</p>
      <p>User's Name: {user_name}</p>
      <Box borderBottom="1px solid gray" mx="5%" />
      <p>Tab Items: </p>
      {items.map((item) => (
        <div>
          <Item
            id={item.id}
            name={item.name}
            price={item.price}
            date={item.created_at}
          />
        </div>
      ))}
      <Box borderBottom="1px solid gray" mx="5%" />
      <p>Add a new Item</p>
      <CreateItem tab_id={id} />
    </Box>
  );
}

function Item({ id, name, price, date }) {
  return (
    <div>
      <p>name: {name}</p>
      <p>price: {price}</p>
      <p>date: {date}</p>
    </div>
  );
}

function CreateItem({ tab_id }) {
  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
  const [price, setPrice] = useState([]);
  const [message, setMessage] = useState([]);

  const handleChange = (event) => {
    if ([event.target.name] == "name") {
      setName(event.target.value);
    } else if ([event.target.name] == "price") {
      setPrice(event.target.value);
    }
  };

  const newItem = async (e) => {
    e.preventDefault();
    const item = {
      tab_id: tab_id,
      category_id: 1,
      name: name,
      price: parseInt(price),
    };
    console.log(item);
    const response = await fetch(`http://tabsystem.test/api/item`, {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "content-type": "application/json",
      },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
      body: JSON.stringify(item),
    });
    const data = await response.json();
    console.log(data);
    if (data.success) {
      setMessage("Successfully created item");
    }
  };
  return (
    <div>
      <h3>Put a new item</h3>
      <div>
        <form onSubmit={newItem}>
          <label>Item Name</label>
          <input type="text" onChange={handleChange} value={name} name="name" />
          <label>Price</label>
          <input
            type="number"
            onChange={handleChange}
            value={price}
            name="price"
          />
          <button type="submit">Submit</button>
        </form>
      </div>

      {message}
    </div>
  );
}
