//state handler imports
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useQuery } from "react-query";
import { GetTabById } from "../../Api/Tabs";
import { GetItems } from "../../Api/Items";
import { GetCategories } from "../../Api/Categories";
import { GetCommonItems } from "../../Api/CommonItems";
import { GetTabsUser } from "../../Api/Users";

//styled component imports
import { Box, Icon, Grid, Skeleton, Spinner } from "@chakra-ui/core";

//component imports
import TabCard from "./TabCard";

const fetchOneTab = async (id) => {
  const response = await fetch(`http://tabsystem.test/api/tab/${id}`, {
    method: "Get",
    mode: "cors", // no-cors, *cors, same-origin
    cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
    credentials: "omit", // include, *same-origin, omit
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
      "content-type": "application/json",
    },
    redirect: "follow", // manual, *follow, error
    referrerPolicy: "no-referrer", // no-referrer, *client
  });

  const data = await response.json();
  return data;
};

export default function TabDetail({ history, match }) {
  //set my id state which is either 1 or the id specified in the url
  const [id, setId] = useState(
    !match.params.id ? 1 : parseInt(match.params.id)
  );

  //fetch the data using id as a paramerter to find what data to return.
  const { status: status1, data: tabData } = useQuery(
    ["GetTabById", id],
    GetTabById,
    {}
  );
  const { status: status2, data: itemsData } = useQuery(
    ["GetItems", id],
    GetItems,
    {}
  );
  const { status: status3, data: categoriesData } = useQuery(
    ["GetCategories", id],
    GetCategories,
    {}
  );
  const { status: status4, data: commonitemsData } = useQuery(
    ["GetCommonItems", id],
    GetCommonItems,
    {}
  );
  const { status: status5, data: UserData } = useQuery(
    ["GetTabsUser", id],
    GetTabsUser,
    {}
  );

  //return this when loading
  if (
    status1 === "loading" ||
    status2 === "loading" ||
    status3 === "loading" ||
    status4 === "loading" ||
    status5 === "loading"
  ) {
    return (
      <div className="">
        <div>
          <Box textAlign="center" fontSize="50px" mt="60px">
            Loading... <Spinner />
          </Box>
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
        </div>
      </div>
    );
  }
  //return when error
  if (
    status1 === "error" ||
    status2 === "error" ||
    status3 === "error" ||
    status4 === "error" ||
    status5 === "error"
  ) {
    return <div className="">error {error}</div>;
  }

  return (
    <div>
      <Link to={`/tabs`}>Back</Link>
      <TabCard
        id={tabData.tab.id}
        name={tabData.tab.name}
        user_name={data.tab.users.name}
        balance={data.tab.balance}
        site_number={data.tab.users.site_number}
        items={data.tab.items}
        categories={categoriesData}
        common_items={commonitemsData}
      />
    </div>
  );
}
