import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useQuery } from "react-query";

//styled component imports
import { Box, Icon, Grid, Skeleton, Spinner } from "@chakra-ui/core";

/** Returns the data from the api https://intern-pokedex.myriadapps.com/api/v1/pokemon/${id}*/
const fetchData = async (key) => {
  const response = await fetch(`http://tabsystem.test/api/category`, {
    method: "Get",
    mode: "cors", // no-cors, *cors, same-origin
    cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
    credentials: "omit", // include, *same-origin, omit
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
      "content-type": "application/json",
    },
    redirect: "follow", // manual, *follow, error
    referrerPolicy: "no-referrer", // no-referrer, *client
  });
  const data = await response.json();
  console.log(data);
  return data;
};

export default function CreateCategory() {
  const [name, setName] = useState([]);
  const [message, setMessage] = useState("");
  const { status, data = [], error, refetch } = useQuery("key", fetchData, {});

  const handleChange = (event) => {
    setName(event.target.value);
  };
  const newCategory = async (e) => {
    e.preventDefault();
    const category = {
      name: name,
    };
    console.log(category);
    const response = await fetch(`http://tabsystem.test/api/category`, {
      method: "POST",
      mode: "cors", // no-cors, *cors, same-origin
      cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
      credentials: "omit", // include, *same-origin, omit
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "content-type": "application/json",
      },
      redirect: "follow", // manual, *follow, error
      referrerPolicy: "no-referrer", // no-referrer, *client
      body: JSON.stringify(category),
    });
    const data = await response.json();
    console.log(data);
    if (data.success) {
      setMessage("Successfully created item");
    } else {
      setMessage("Something went wrong");
    }
  };
  //return this when loading
  if (status === "loading") {
    return (
      <div className="">
        <div>
          <Box textAlign="center" fontSize="50px" mt="60px">
            Loading... <Spinner />
          </Box>
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
          <Skeleton height="50px" my="10px" mx="10%" />
        </div>
      </div>
    );
  }
  //return when error
  if (status === "error") return <div className="">error {error}</div>;
  console.log("logged something");
  if (!data.categories) {
    refetch();
  }
  return (
    <div>
      <Link to={`/tabs`}>Back</Link>
      {data.categories.map((category) => (
        <div key={category.id}>{category.name}</div>
      ))}
      <div>
        <form onSubmit={newCategory}>
          <label>category name</label>
          <input type="text" onChange={handleChange} value={name} name="name" />

          <button type="submit">Submit</button>
        </form>
        {message}
      </div>
    </div>
  );
}
